def chongxindic(dic):  
    dic2 = {}
    a = 0
    for k,v in dic.items():
        dic2[a] = v
        a += 1
    return dic2
def ziduandic(html): #把所有的字段以表格为单位编辑成字典，即：dic[<表格序号>][<表格里面的字段的序号>]
    html = etree.HTML(html)
    dic = {}
    i = 0
    while True:
        s = '//main/div/table['+str(7+i)+']/tbody/tr/td[1]/code'
        print(s)
        data = bianmabianhuan(html.xpath(s))
        if (data == {}):
            break
        else :
            dic[i] = data
            i += 1
    return dic
def bianmabianhuan(li):
    data = {}
    for i in range(len(li)):
        ent_text = etree.tostring(li[i],method='text',encoding='utf-8')
        data[i] = ent_text.decode()
    return data
def dxie(dic):  #将字典中每个元素第一个字符不是‘/’的删除
    for i in range(len(dic)):
        if(dic[i][0] != '/'):
            del dic[i]
        else:
            pass

    return chongxindic(dic)
